import { Box, Grid, Paper, Typography, Card, CardActionArea, CardContent } from '@mui/material';
import { WifiOffTwoTone, WarningTwoTone, HotelTwoTone, NotificationsActiveTwoTone, AirlineSeatReclineNormalTwoTone, DirectionsRunTwoTone, CancelPresentationTwoTone } from '@mui/icons-material';
import { yellow, grey, red, green } from '@mui/material/colors';
import { styled } from '@mui/system';
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import SensorPanel from './SensorPanel';

const checkColor = (state) => {
    var color = '#FFF'
    switch (state) {
        case 'Off':
            color = grey[500]; break;
        case 'No connection':
            color = red[200]; break;
        case 'No signal':
            color = red[200]; break;
        case 'Reposition':
            color = green[200]; break;
        default:
            color = grey[200];
    }
    return color
}

const StyledBedBox = styled(Box)({
    backgroundColor: '#EEEEEE',
    padding: 10,
    border: '1px dashed grey'
});

const StyledPaper = styled(Paper)(({ theme }) => ({
    display: 'flex',
    justifyContent: 'center',
    marginBottom: theme.spacing(1),
    backgroundColor: yellow[300],
    fontSize: "30px",
}));

const StyledCard = styled(Card)(({state}) =>({
    backgroundColor: checkColor(state)
}));

const StyledCardDiv = styled('div')(({theme})=>({
    display: 'flex',
    justifyContent: 'center',
    marginBottom: theme.spacing(1),
}));

const styledIcon = {
    fontSize: "150px",
}

const BedDetail = (props) => {
    const params = useParams()
    {/* {params.bedId} */ }
    return (

        <Grid container spacing={2} columns={5}>
            <Grid item xs={4}>
                <StyledPaper>
                    <Typography variant='h5'>
                        {params.bedId}
                    </Typography>
                </StyledPaper>
                <StyledBedBox>
                    <Grid container spacing={2} columns={3}>
                        <Grid item xs={1}>
                            <SensorPanel panel_index='2' />
                        </Grid>
                        <Grid item xs={1}>
                            <SensorPanel panel_index='2' />
                        </Grid>
                        <Grid item xs={1}>
                            <SensorPanel panel_index='3' />
                        </Grid>
                    </Grid>
                </StyledBedBox>
            </Grid>
            <Grid item xs={1}>
                <StyledCard>
                    <CardActionArea>
                        <CardContent>
                            <StyledCardDiv>
                                <BedIcon sx={styledIcon} state={props.info.state} />
                            </StyledCardDiv>
                            <StyledCardDiv>
                                <Typography variant='h6'>
                                    {props.info.state}
                                </Typography>
                            </StyledCardDiv>
                            <StyledCardDiv>
                                <StyledCardTime variant='h6'>
                                    00:00:00
                                </StyledCardTime>
                            </StyledCardDiv>
                        </CardContent>
                    </CardActionArea>
                </StyledCard>
            </Grid>
        </Grid >

    );
};

export default BedDetail;
