import { Box, Grid, Paper, Typography, Card, CardActionArea, CardContent, Button } from '@mui/material';
import { yellow } from '@mui/material/colors';
import { styled } from '@mui/system';
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import SensorPanel from './SensorPanel';
import { BedIcon, checkColor } from './BedCard';

const StyledBedBox = styled(Box)({
    backgroundColor: '#EEEEEE',
    padding: 10,
    border: '1px dashed grey'
});

const StyledPaper = styled(Paper)(({ theme }) => ({
    display: 'flex',
    justifyContent: 'center',
    marginBottom: theme.spacing(1),
    backgroundColor: yellow[300],
    fontSize: "30px",
}));

const StyledCard = styled(Card)(({ state }) => ({
    backgroundColor: checkColor(state)
}));

const StyledCardDiv = styled('div')(({ theme }) => ({
    display: 'flex',
    justifyContent: 'center',
    marginBottom: theme.spacing(1),
}));

const styledIcon = {
    fontSize: "150px",
}

const StyledCardTime = styled('div')(({ state }) => ({
    visibility: state == 'Reposition' ? 'visible' : 'hidden'
}));

const BedDetail = (props) => {
    const params = useParams()
    {/* {params.bedId} */ }
    return (

        <Grid container spacing={2} columns={5}>
            <Grid item xs={4}>
                <StyledPaper>
                    <Typography variant='h5'>
                        {params.bedId}
                    </Typography>
                </StyledPaper>
                <StyledBedBox>
                    <Grid container spacing={2} columns={3}>
                        <Grid item xs={1}>
                            <SensorPanel panel_index='2' />
                        </Grid>
                        <Grid item xs={1}>
                            <SensorPanel panel_index='2' />
                        </Grid>
                        <Grid item xs={1}>
                            <SensorPanel panel_index='3' />
                        </Grid>
                    </Grid>
                </StyledBedBox>
            </Grid>
            <Grid item xs={1}>
                <Grid container spacing={2} columns={12}>
                    <Grid item xs={12}>
                        <StyledCard state={"Reposition"}>
                            <CardActionArea>
                                <CardContent>
                                    <StyledCardDiv>
                                        <BedIcon sx={styledIcon} state={"Reposition"} />
                                    </StyledCardDiv>
                                    <StyledCardDiv>
                                        <Typography variant='h6'>
                                            {"Reposition"}
                                        </Typography>
                                    </StyledCardDiv>
                                    <StyledCardDiv>
                                        <StyledCardTime variant='h6' state={"Reposition"}>
                                            00:00:00
                                        </StyledCardTime>
                                    </StyledCardDiv>
                                </CardContent>
                            </CardActionArea>
                        </StyledCard>
                    </Grid>
                    <Grid item xs={12}><Button variant="contained">Mute</Button></Grid>
                    <Grid item xs={12}><Button>Reset</Button></Grid>
                    <Grid item xs={12}><Button>Off</Button></Grid>
                </Grid>
            </Grid>
        </Grid >

    );
};

export default BedDetail;
